create view ViewListOfContractSalesman as
select ContractSalesman = '['+u.SalesCode +'] ' + u.LastName + ' '+ u.FirstName, Channel = ct.Description, CaseID = c.ID 
from [case] c (nolock)
join [case] cc (nolock) on c.MasterCaseID = cc.ID and cc.ContextEntityType = 'TGIV'
left outer join TargetGroupItemValue tgiv (nolock) on cc.ContextEntityID = tgiv.ID
left outer join [Contract] con (nolock) on COALESCE(tgiv.ContractID,tgiv.AltOfferID) = con.ID
left outer join ChannelType ct (nolock) on con.ChannelTypeID = ct.ID
left outer join [User] u (nolock) on con.SalesManID = u.ID
union
select ContractSalesman = '['+u.SalesCode +'] ' + u.LastName + ' '+ u.FirstName, Channel = ct.Description, CaseID = c.ID 
from [case] c (nolock)
join [case] cc (nolock) on c.MasterCaseID = cc.ID and cc.ContextEntityType = 'TICKET'
left outer join Ticket tic (nolock) on cc.ContextEntityID = tic.ID
left outer join [Contract] con (nolock) on tic.RelatedContractID = con.ID 
left outer join ChannelType ct (nolock) on con.ChannelTypeID = ct.ID
left outer join [User] u (nolock) on con.SalesManID = u.ID
union
select ContractSalesman = null, Channel = null, CaseID = c.ID
from [case] c (nolock)
join [case] cc (nolock) on c.MasterCaseID = cc.ID 
and cc.ContextEntityType <> 'TICKET' 
and cc.ContextEntityType <> 'TGIV'

